import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';

export default function ChartTest() {
  const chartContainerRef = useRef(null);

  useEffect(() => {

    if (!chartContainerRef.current) return;

    const chart = createChart(chartContainerRef.current, {
      width: 800,
      height: 400,
      layout: {
        background: { color: '#222' },
        textColor: '#DDD',
      },
      grid: {
        vertLines: { color: '#444' },
        horzLines: { color: '#444' },
      },
    });

    const candlestickSeries = chart.addCandlestickSeries({
      upColor: '#26a69a',
      downColor: '#ef5350',
      borderVisible: false,
      wickUpColor: '#26a69a',
      wickDownColor: '#ef5350',
    });

    // Simple test data
    const data = [
      { time: '2023-01-01', open: 100, high: 110, low: 95, close: 105 },
      { time: '2023-01-02', open: 105, high: 115, low: 100, close: 110 },
      { time: '2023-01-03', open: 110, high: 120, low: 105, close: 108 },
      { time: '2023-01-04', open: 108, high: 112, low: 102, close: 106 },
      { time: '2023-01-05', open: 106, high: 116, low: 104, close: 114 },
    ];

    candlestickSeries.setData(data);
    chart.timeScale().fitContent();

    return () => {
      chart.remove();
    };
  }, []);

  return (
    <div style={{ padding: '50px', background: '#000', minHeight: '100vh' }}>
      <h1 style={{ color: '#FFF' }}>Chart Test - If you see candles below, the library works</h1>
      <div ref={chartContainerRef} style={{ marginTop: '20px' }} />
    </div>
  );
}
